import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [senderEmail, setSenderEmail] = useState('');
  const [senderPassword, setSenderPassword] = useState('');
  const [recipientEmails, setRecipientEmails] = useState('');
  const [subject, setSubject] = useState('');
  const [body, setBody] = useState('');
  const [pdfFile, setPdfFile] = useState(null);
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Convert the PDF file to base64
    const reader = new FileReader();
    reader.readAsDataURL(pdfFile);
    reader.onload = async () => {
      const pdfBase64 = reader.result.split(',')[1]; // Remove the data URL prefix

      // Prepare the data to send to the backend
      const data = {
        sender_email: senderEmail,
        sender_password: senderPassword, // Send the password directly
        recipient_emails: recipientEmails.split(',').map(email => email.trim()),
        subject,
        body,
        pdf_file_base64: pdfBase64,
        pdf_file_name: pdfFile.name,
      };

      try {
        // Send the data to the backend
        const response = await fetch('http://127.0.0.1:5001/send-email', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(data),
        });

        const result = await response.json();
        if (response.ok) {
          setMessage(result.message);
        } else {
          setMessage(result.error);
        }
      } catch (error) {
        setMessage('Failed to send emails.');
      }
    };
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header bg-primary text-white">
              <h1 className="card-title text-center">Send Email</h1>
            </div>
            <div className="card-body">
              <form onSubmit={handleSubmit}>
                {/* Sender Email Field */}
                <div className="mb-3">
                  <label htmlFor="senderEmail" className="form-label">
                    Sender Email:
                  </label>
                  <input
                    type="email"
                    className="form-control"
                    id="senderEmail"
                    value={senderEmail}
                    onChange={(e) => setSenderEmail(e.target.value)}
                    required
                  />
                </div>

                {/* Sender Password Field */}
                <div className="mb-3">
                  <label htmlFor="senderPassword" className="form-label">
                    App Password:
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="senderPassword"
                    value={senderPassword}
                    onChange={(e) => setSenderPassword(e.target.value)}
                    required
                  />
                </div>

                {/* Recipient Emails Field */}
                <div className="mb-3">
                  <label htmlFor="recipientEmails" className="form-label">
                    Recipient Emails (comma-separated):
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="recipientEmails"
                    value={recipientEmails}
                    onChange={(e) => setRecipientEmails(e.target.value)}
                    required
                  />
                </div>

                {/* Subject Field */}
                <div className="mb-3">
                  <label htmlFor="subject" className="form-label">
                    Subject:
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="subject"
                    value={subject}
                    onChange={(e) => setSubject(e.target.value)}
                    required
                  />
                </div>

                {/* Body Field */}
                <div className="mb-3">
                  <label htmlFor="body" className="form-label">
                    Body:
                  </label>
                  <textarea
                    className="form-control"
                    id="body"
                    rows="5"
                    value={body}
                    onChange={(e) => setBody(e.target.value)}
                    required
                  />
                </div>

                {/* PDF File Field */}
                <div className="mb-3">
                  <label htmlFor="pdfFile" className="form-label">
                    Attach PDF:
                  </label>
                  <input
                    type="file"
                    className="form-control"
                    id="pdfFile"
                    accept="application/pdf"
                    onChange={(e) => setPdfFile(e.target.files[0])}
                    required
                  />
                </div>

                {/* Submit Button */}
                <div className="d-grid">
                  <button type="submit" className="btn btn-primary">
                    Send Email
                  </button>
                </div>
              </form>

              {/* Display Message */}
              {message && (
                <div className="mt-3 alert alert-info">
                  {message}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
